import React from 'react';
import { View, ActivityIndicator, Button} from 'react-native';
import VisualizaAlunosBanco from '../../../API/VisualizaTurma/visualizaAlunosBanco';
import {useEffect, useState} from "react";

export default function VizualizaAlunos({route}){

    const [loading, setLoading] = useState(false)
    const [erro, setErro] = useState("")
    const [reload, setReload] = useState(false)

    const visualizar = () =>{

        setLoading(true)
        setTimeout(() =>{
            setLoading(false)
        },10000)
    }

    const recarregar= () =>{
        setReload(true)
    }
    
    try {
        <VisualizaAlunosBanco disciplina={route.params.paramKey} navigation={route.params.navigation}/>
    } catch (error) {
        <Button title='Recarregar pÃ¡gina' onPress={()=>recarregar()} />
        setErro('Erro ao visualizar alunos')
        console.log(erro);
     }
        
    useEffect(
        () => {
            VizualizaAlunos()
        }, [reload == true]
    );

        
        return(     

            <view>

                <view>
                    <ActivityIndicator size="large" color="blue" animating={loading} />
                    <Button title='Visualizar' onPress={() => visualizar()} />
                </view>
            
                <view style={{ marginLeft: '300', marginRight: '300' }}>
                    <VisualizaAlunosBanco disciplina={route.params.paramKey} navigation={route.params.navigation} />
                </view>

            </view>
        )
}
