import React from 'react';
import { View, ActivityIndicator, Button, Text} from 'react-native';
import VisualizaAlunosBanco from '../../../API/VisualizaTurma/visualizaAlunosBanco';

export default function VizualizaAlunos({route}){

    const [loading, setLoading] = useState(true)

    setTimeout(() =>{
        setLoading(false)
    },2000)

    const alerta = () =>{
        Alert.alert("Erro ao carregar página")
    }

        if(loading == true){
            return(
                <view>
                    <ActivityIndicator size="large" color="blue" animating={loading} />
                </view>
            )
        } 
        
    if(loading == false){

        try {
            return(
                <View>
                <VisualizaAlunosBancoa disciplina={route.params.paramKey} navigation={route.params.navigation} />
                </View>  
            )
        } catch (error) {
            return(
                <View>
                <Text>  </Text>
                <Button title='Tente novamente' onPress={()=> {alerta(), setLoading(true)}} />
                </View>
            )
        }

    }   

}
