import React from 'react';
import { View, Text, ActivityIndicator, Button, Alert} from 'react-native';
import VisualizaAlunosBanco from '../../../API/VisualizaTurma/visualizaAlunosBanco';

export default function VizualizaAlunos({route}){

    const [loading, setLoading] = useState(false);
        

    const visualizar = () =>{

        setLoading(true)
        setTimeout(() =>{
            setLoading(false)
            Alert.alert("Erro ao carregar alunos!")
        },10000)
    }
        <view>
            <ActivityIndicator 
                size="large" 
                color="blue"
                animating={loading}
            />
            <Text> Loading... </Text>
            <Button title='Visualizar' onPress={()=>visualizar()}/>
        </view>
        
    // loading = false
    // if(loading == false){
        return(     
        
            <View style={{marginLeft:'300', marginRight:'300'}}>
                <VisualizaAlunosBanco disciplina={route.params.paramKey} navigation={route.params.navigation}/>
            </View>
        )
}
