import React from 'react';
import { View, ActivityIndicator, Button, Alert} from 'react-native';
import VisualizaAlunosBanco from '../../../API/VisualizaTurma/visualizaAlunosBanco';
import {useEffect, useState} from "react";

export default function VizualizaAlunos({route}){

    const [loading, setLoading] = useState(true)

    setTimeout(() =>{
        setLoading(false)
    },2000)

    const alerta = () =>{
        Alert.alert("Erro ao carregar página")
    }

        if(loading == true){
            return(
                <view>
                    <ActivityIndicator size="large" color="blue" animating={loading} />
                </view>
            )
        } 
        
    if(loading == false){

        try {
            return(
                <View>
                <VisualizaAlunosBancoa disciplina={route.params.paramKey} navigation={route.params.navigation} />
                </View>  
            )
        } catch (error) {
            return(
                <Button title='Recarregar página' onPress={()=> {setLoading(true), alerta()}} />
            )
        }

    }   

}
